import React, { Component } from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
      formData: {
        capShape: '',
        capSurface: '',
        capColour: 1,
        bruises: 1,
        gillAttachment: 1,
        gillSpacing: 1,
        gillSize: 1,
        gillColour: 1,
        stalkShape: 1,
        stalkRoot: 1,
        stalkSurfaceAboveRing: 1,
        stalkColourBelowRing: 1,
        veilType: 1,
        veilColour: 1,
        ringNumber: 1,
        ringType: 1,
        sporePrintColour: 1,
        population: 1,
        habitat: 1
      },
      result: "Click predict to find out!"
    };
  }

  handleChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    var formData = this.state.formData;
    formData[name] = value;
    this.setState({
      formData
    });
  }

  handlePredictClick = (event) => {
    const formData = this.state.formData;
    this.setState({ isLoading: true });
    fetch('http://127.0.0.1:5000/prediction/',
      {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        method: 'POST',
        body: JSON.stringify(formData)
      })
      .then(response => response.json())
      .then(response => {
        this.setState({
          result: response.result,
          isLoading: false
        });
      });
  }

  handleCancelClick = (event) => {
    this.setState({ result: "" });
  }

  render() {
    const isLoading = this.state.isLoading;
    const formData = this.state.formData;
    const result = this.state.result;

    return (
      <Container>
        <div>
          <h1 className="title">Can eat this mushroom?</h1>
        </div>
        <div className="content">
          <Form>
            <Form.Row>
              <Form.Group as={Col}>
                <Form.Label>Cap Shape</Form.Label>
                <Form.Control
                  as="select"
                  value={formData.select1}
                  name="select1"
                  onChange={this.handleChange}>
                  <option>Bell</option>
                  <option>Conical</option>
                  <option>Convex</option>
                  <option>Flat</option>
                  <option>Knobbed</option>
                  <option>Sunken</option>
                </Form.Control>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Cap Surface</Form.Label>
                <Form.Control
                  as="select"
                  value={formData.select2}
                  name="select2"
                  onChange={this.handleChange}>
                  <option>Fibrous</option>
                  <option>Grooves</option>
                  <option>Scaly</option>
                  <option>Smooth</option>
                </Form.Control>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Bruises</Form.Label>
                <Form.Control
                  as="select"
                  value={formData.select3}
                  name="select3"
                  onChange={this.handleChange}>
                  <option>Yes</option>
                  <option>No</option>
                </Form.Control>
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col}>
                <Form.Label>Odour</Form.Label>
                <Form.Control 
                  as="select"
                  value={formData.select4}
                  name="select4"
                  onChange={this.handleChange}>
                  <option>Almond</option>
                  <option>Anise</option>
                  <option>Creosote</option>
                  <option>Fishy</option>
                  <option>Foul</option>
                  <option>Musty</option>
                  <option>None</option>
                  <option>Pungent</option>
                  <option>Spicy</option>
                </Form.Control>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Gill Attachment</Form.Label>
                <Form.Control 
                  as="select"
                  value={formData.select5}
                  name="select5"
                  onChange={this.handleChange}>
                  <option>Attached</option>
                  <option>Descending</option>
                  <option>Free</option>
                  <option>Notched</option>
                </Form.Control>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Gill Spacing</Form.Label>
                <Form.Control 
                  as="select"
                  value={formData.select6}
                  name="select6"
                  onChange={this.handleChange}>
                  <option>Close</option>
                  <option>Crowded</option>
                  <option>Distant</option>
                </Form.Control>
              </Form.Group>
            </Form.Row>
            <Row>
              <Col>
                <Button
                  block
                  variant="success"
                  disabled={isLoading}
                  onClick={!isLoading ? this.handlePredictClick : null}>
                  { isLoading ? 'Making prediction' : 'Predict' }
                </Button>
              </Col>
              <Col>
                <Button
                  block
                  variant="danger"
                  disabled={isLoading}
                  onClick={this.handleCancelClick}>
                  Try Another
                </Button>
              </Col>
            </Row>
          </Form>
          {result === "" ? null :
            (<Row>
              <Col className="result-container">
                <h5 id="result">{result}</h5>
              </Col>
            </Row>)
          }
        </div>
      </Container>
    );
  }
}

export default App;
